import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent  {
  products: Product[];

  constructor() {
    this.products = [
      new Product(
        'MYSHOES',
        'Black Running Shoes',
        '/assets/img/nike.jpeg',
        ['Men', 'Shoes', 'Runings'],
        109.99
      ),
      new Product(
        'BAT',
        'A Nice Black Hat',
        '/assets/img/Bat.jpg',
        ['Men', 'Apparel', 'bat'],
        29.99
      ),
      new Product(
        'NICHET',
        'A Nice Black Hat',
        '/assets/img/product/black-hat.png',
        ['Men', 'Accessories', 'Hats'],
        29.99
      )
    ];
  }
}

export class Product{
id: string;
name: string;
image: string;
size: string[];
price: number;

constructor(id:any, name:any, image:any, size:any, price:any){
this.id = id;
this.name = name;
this.image = image;
this.size = size;
this.price = price;
}
}
