import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent  {
  products: Product[];

  constructor() {
    this.products = [
      new Product(
        '1',
        'Black Lentils',
        'src/app/assets/img/black_lentils.jpg',
        ['Small - 2 lbs'],
       200
      ),
      new Product(
        '2',
        'Brown Winter Shoes',
        '/assets/img/shoes.jpg',
        ['10', '11', '12', '13'],
        642
      ),
      new Product(
        '3',
        'Tissue Papers',
        '/assets/img/product/tissues.jpg',
        [''],
        2000
      )
    ];
  }
}

export class Product{
id: string;
name: string;
image: string;
size: string[];
price: number;

constructor(id:any, name:any, image:any, size:any, price:any){
this.id = id;
this.name = name;
this.image = image;
this.size = size;
this.price = price;
}
}
